package aoc2024.day12

import aoc2024.Coord
import aoc2024.Grid2d

fun main() {
    val grid = Grid2d(actualInput) { it }
    part1(grid) // 1434856
}

fun part1(grid: Grid2d<Char>) {
    val regions = mutableListOf<Set<Coord>>()
    val visited = mutableSetOf<Coord>()

    grid.getCoords().forEach { coord ->
        if (coord !in visited) {
            val region = findRegion(grid, coord)
            regions.add(region)
            visited.addAll(region)
        }
    }

    println(regions.sumOf { calculateSides(it) * it.size })
}

fun findRegion(grid: Grid2d<Char>, coord: Coord, region: MutableSet<Coord> = mutableSetOf(coord)): Set<Coord> {
    val newCoords = coord.cardinalNeighbors().filter { it !in region && grid[it] == grid[coord] }
    if (newCoords.isEmpty()) {
        return region
    }
    region.addAll(newCoords)
    return newCoords.flatMap { findRegion(grid, it, region) }.toSet()
}

fun calculateSides(region: Set<Coord>): Int {
    return region.flatMap { it.cardinalNeighbors() }
        .count { it !in region }
}

